import assert from 'assert';

export default function Item(weight, value) {
	assert(weight > 0);
	this.w = weight;
	this.v = value;
}

import Item from './Item';
import integerValuesKnapsack from './integerValuesKnapsack';
import integerValuesKnapsackUnbounded from './integerValuesKnapsackUnbounded';
import integerWeightsKnapsack from './integerWeightsKnapsack';
import integerWeightsKnapsackUnbounded from './integerWeightsKnapsackUnbounded';
import knapsackApprox from './knapsackApprox';
import knapsackGreedy from './knapsackGreedy';
import knapsackUnboundedGreedy from './knapsackUnboundedGreedy';
import orderedByDecreasingUtility from './orderedByDecreasingUtility';

/* eslint import/no-anonymous-default-export: [2, {"allowObject": true}] */
export default {
	Item,
	integerValuesKnapsack,
	integerValuesKnapsackUnbounded,
	integerWeightsKnapsack,
	integerWeightsKnapsackUnbounded,
	knapsackApprox,
	knapsackGreedy,
	knapsackUnboundedGreedy,
	orderedByDecreasingUtility,
};

export {
	Item,
	integerValuesKnapsack,
	integerValuesKnapsackUnbounded,
	integerWeightsKnapsack,
	integerWeightsKnapsackUnbounded,
	knapsackApprox,
	knapsackGreedy,
	knapsackUnboundedGreedy,
	orderedByDecreasingUtility,
};

import assert from 'assert';
import {sum} from '@aureooms/js-itertools';

/**
 * Exact DP solution to the 0-1 knapsack problem with integer values given a known
 * upper bound V on OPT. Runs in O(nV) time.
 *
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 * @param {Number} V Any upper bound on OPT >= 0.
 * @param {Array} m Memory buffer.
 */
const integerValuesKnapsack = (
	v,
	w,
	n,
	W,
	V = sum(v),
	m = new w.constructor(V + 1).fill(W + 1),
) => {
	assert(v.length === n);
	assert(w.length === n);
	assert(Number.isInteger(V) && V >= 0);
	assert(m.length >= V + 1);
	m[V] = 0;
	for (let i = 0; i < n; ++i) {
		const wi = w[i];
		const vi = v[i];
		assert(Number.isInteger(vi) && vi >= 0);
		const k = V - vi + 1;
		for (let j = 0; j < k; ++j) {
			m[j] = Math.min(m[j], m[j + vi] + wi);
		}
	}

	for (let j = 0; j < V; ++j) {
		if (m[j] <= W) return V - j;
	}

	return 0;
};

export default integerValuesKnapsack;

import assert from 'assert';
import {increasing} from '@aureooms/js-compare';
import {max, map, range} from '@aureooms/js-itertools';

/**
 * Exact DP solution to the unbounded knapsack problem with integer values
 * given a known upper bound V on OPT. Runs in O(nV) time.
 *
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 * @param {Number|BigInt} zero The number 0.
 * @param {Number} V Any upper bound on OPT >= 0.
 * @param {Array} m Memory buffer.
 */
const integerValuesKnapsackUnbounded = (
	v,
	w,
	n,
	W,
	zero = 0,
	V = Math.floor(
		W *
			max(
				increasing,
				map((i) => v[i] / w[i], range(n)),
				zero,
			),
	),
	m = new w.constructor(V + 1).fill(W + 1),
) => {
	assert(v.length === n);
	assert(w.length === n);
	assert(Number.isInteger(V) && V >= 0);
	assert(m.length >= V + 1);
	m[0] = 0;
	for (let j = 1; j <= V; ++j) {
		let temporary = m[j];
		for (let i = 0; i < n; ++i) {
			const wi = w[i];
			const vi = v[i];
			assert(Number.isInteger(vi) && vi >= 0);
			const k = j - vi;
			// TODO sort by value to avoid branching
			// from larger to smaller so that m is scanned
			// from left to right
			if (k >= 0) temporary = Math.min(temporary, m[k] + wi);
		}

		m[j] = temporary;
	}

	for (let j = V; j > 0; --j) {
		if (m[j] <= W) return j;
	}

	return 0;
};

export default integerValuesKnapsackUnbounded;

import assert from 'assert';

/**
 * Exact DP solution to the 0-1 knapsack problem with integer weights.
 * Runs in O(nW) time.
 *
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 * @param {Array} m Memory buffer.
 */
const integerWeightsKnapsack = (
	v,
	w,
	n,
	W,
	m = new v.constructor(W + 1).fill(0),
) => {
	assert(v.length === n);
	assert(w.length === n);
	assert(Number.isInteger(W) && W >= 0);
	assert(m.length >= W + 1);
	for (let i = 0; i < n; ++i) {
		const wi = w[i];
		const vi = v[i];
		assert(Number.isInteger(wi) && wi >= 0 && wi <= W);
		const k = W - wi + 1;
		for (let j = 0; j < k; ++j) {
			m[j] = Math.max(m[j], m[j + wi] + vi);
		}
	}

	return m[0];
};

export default integerWeightsKnapsack;

import assert from 'assert';

/**
 * Exact DP solution to the unbounded knapsack problem with integer weights.
 * Runs in O(nW) time.
 *
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 * @param {Array} m Memory buffer.
 */
const integerWeightsKnapsackUnbounded = (
	v,
	w,
	n,
	W,
	m = new v.constructor(W + 1).fill(0),
) => {
	assert(v.length === n);
	assert(w.length === n);
	assert(Number.isInteger(W) && W >= 0);
	assert(m.length >= W + 1);
	for (let j = 1; j <= W; ++j) {
		let temporary = m[j];
		for (let i = 0; i < n; ++i) {
			const wi = w[i];
			const vi = v[i];
			assert(Number.isInteger(wi) && wi >= 0 && wi <= W);
			const k = j - wi;
			// TODO sort by weight to avoid branching
			// from larger to smaller so that m is scanned
			// from left to right
			if (k >= 0) temporary = Math.max(temporary, m[k] + vi);
		}

		m[j] = temporary;
	}

	return m[W];
};

export default integerWeightsKnapsackUnbounded;

import assert from 'assert';
import {increasing} from '@aureooms/js-compare';
import {max, map, filter, range} from '@aureooms/js-itertools';

import integerValuesKnapsack from './integerValuesKnapsack';

/**
 * (1-eps)-approx for the knapsack problem. Runs in O(N^3/eps) time.
 *
 * @param {Number} eps Approximation constant.
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 * @param {Number} P Any lower bound on OPT > 0.
 */
const knapsackApprox = (
	eps,
	v,
	w,
	n,
	W,
	P = max(
		increasing,
		map(
			(i) => v[i],
			filter((i) => w[i] <= W, range(n)),
		),
	),
) => {
	assert(eps > 0 && eps < 1);
	assert(P > 0);
	assert(v.length === n);
	assert(w.length === n);
	const K = (eps * P) / n;
	const u = Uint32Array.from(v, (vi) => Math.floor(vi / K));
	// TODO reconstruct solution
	return K * integerValuesKnapsack(u, w, n, W);
};

export default knapsackApprox;

import assert from 'assert';
import {map, range, sorted, filter} from '@aureooms/js-itertools';

import Item from './Item';
import orderedByDecreasingUtility from './orderedByDecreasingUtility';

/**
 * 1/2-approximation to the 0-1 knapsack problem.
 * Runs in O(n log n) time.
 *
 * Let OPT' be the value of the LP relaxation.
 * Let v_i be the values of the items ordered by utility.
 * Let k be the largest k such that sum(v[:k]) <= W.
 * Let t = (W-sum(w[:k])) / w_{k+1},
 * we have t < 1 and OPT' = sum(v[:k]) + t * v_{k+1}.
 * Hence sum(v[:k+1]) > OPT' >= OPT.
 * By partition one of { sum(v[:k]) , v_{k+1} } is at least OPT / 2.
 * Assuming w_i <= W for all i in [N] each of these is feasible.
 *
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 */
const knapsackGreedy = (v, w, n, W) => {
	assert(v.length === n);
	assert(w.length === n);
	assert(W >= 0);

	const items = sorted(
		orderedByDecreasingUtility,
		filter(
			(item) => item.w <= W,
			map((i) => new Item(w[i], v[i]), range(n)),
		),
	);
	return subroutine(W, items);
};

const subroutine = (W, items) => {
	let value = 0;
	let capacity = W;
	for (const {v, w} of items) {
		if (capacity < w) return Math.max(v, value);
		capacity -= w;
		value += v;
	}

	return value;
};

export default knapsackGreedy;

import assert from 'assert';
import {map, range, sorted} from '@aureooms/js-itertools';

import Item from './Item';
import orderedByDecreasingUtility from './orderedByDecreasingUtility';

/**
 * 1/2-approximation to the unbounded knapsack problem.
 * Runs in O(n log n) time.
 *
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 */
const knapsackUnboundedGreedy = (v, w, n, W) => {
	assert(v.length === n);
	assert(w.length === n);
	assert(W >= 0);

	const items = sorted(
		orderedByDecreasingUtility,
		map((i) => new Item(w[i], v[i]), range(n)),
	);
	return subroutine(W, items);
};

const subroutine = (W, items) => {
	let value = 0;
	let capacity = W;
	for (const {v, w} of items) {
		const copies = Math.floor(capacity / w);
		capacity -= copies * w;
		value += copies * v;
	}

	return value;
};

export default knapsackUnboundedGreedy;

import {fn, decreasing} from '@aureooms/js-compare';

const utility = (x) => x.v / x.w;
const orderedByDecreasingUtility = fn(decreasing, utility);

export default orderedByDecreasingUtility;

